jQuery('#course_details_actions').on('PLAN_ITEM_ADDED', function(event, data){
    if (data.planItemType === 'wishlist' && data.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
        actionFeedback(data.courseId + '_addSavedCourse', true, '@{dataObject.courseSummaryDetails.courseId}_bookmarked', data.cssClasses + ' alert-unboxed alert-replace-btn uif-boxLayoutHorizontalItem', data.message)
    }
}).on('PLAN_ITEM_DELETED', function(event, data){
    if (data.planItemType === 'wishlist' && data.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
        restoreDetailsBookmarkButton(data.courseId);
    }
});

