jQuery('#deletePlannedCourse').subscribe('PLAN_ITEM_DELETED', function(data){
    fnDisplayMessage(data.message, data.cssClass, 'deletePlannedCourse', true, true);
}).subscribe('ERROR', function(data) {
    fnDisplayMessage(data.message, data.cssClass, 'deletePlannedCourse', true, true);
});

jQuery('#addPlannedCourse')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        if (data.planItemType === 'planned' || data.planItemType === 'backup') {
            fnDisplayMessage(data.message, data.cssClass, 'plan_add_action_items', true, false);
        }
    })
    .subscribe('ERROR', function(data){
        fnDisplayMessage(data.message, data.cssClass, 'hidden_add_error_block_item', true, false ,true,'hidden_add_error_block_item');
    });

