jQuery('#courseCode_control').bind('keyup paste cut contextmenu mouseout blur', function(e) {
    var display = ( /([0-9]xx)/i.test( jQuery(this).val() ) ) ? 'block' : 'none';
    jQuery('#creditsCourse').css('display', display);
});

