property.value:
getPendingAudit('plan_audits_summary', 'plan');
if (readUrlParam('viewId') == 'PlanAudit-FormView') {
    indicateViewingAudit('plan_audits_summary', 'plan');
}
jQuery('#plan_audits_summary').on('REFRESH_AUDITS AUDIT_COMPLETE', function(event, data) {
    if( data.auditType == 'plan' ) {
        var newAudit = jQuery('.auditReport__html--' + data.auditType + ' .myplan-audit-report').attr('auditid');
        customRetrieveComponent('plan_audits_summary','plan_audits_summary','search','lookup',{viewId:'PlanAuditsSummary-LookupView'}, (newAudit && event.type == 'AUDIT_COMPLETE' ? 'link_' + newAudit : null));
    }
});

var disableCompId = 'plan_audit_validate';
var condition = function () {
    return ((jQuery.cookie('pendingAudit') != null) || (coerceValue('planExists') == false) || (coerceValue('planAudit.campusParam') == '306' && coerceValue('planAudit.programParamSeattle') == 'default') || (coerceValue('planAudit.campusParam') == '310' && coerceValue('planAudit.programParamBothell') == 'default') || (coerceValue('planAudit.campusParam') == '323' && coerceValue('planAudit.programParamTacoma') == 'default'));
};
disabledCheck(disableCompId, 'action', condition);
jQuery('button#' + disableCompId).on('AUDIT_COMPLETE', function (event, data) {
    var condition = function () {
        return ((coerceValue('planExists') == false) || (coerceValue('planAudit.campusParam') == '306' && coerceValue('planAudit.programParamSeattle') == 'default') || (coerceValue('planAudit.campusParam') == '310' && coerceValue('planAudit.programParamBothell') == 'default') || (coerceValue('planAudit.campusParam') == '323' && coerceValue('planAudit.programParamTacoma') == 'default'));
    };
    disabledCheck(jQuery(this).attr('id'), 'action', condition);
});

if (jQuery.cookie('pendingAudit')) {
    if (readUrlParam('planAudit.auditId') == false) {
        var data = jQuery.parseJSON(decodeURIComponent(jQuery.cookie('pendingAudit')));
        blockPendingAudit(data, true);
    }
}
