property.value:
if (jQuery.cookie('myplan_audit_running')) {
    if (readUrlParam('planAudit.auditId') == false) {
        var data = jQuery.parseJSON(decodeURIComponent(jQuery.cookie('myplan_audit_running')));
        blockPendingAudit(data);
    }
}

var disableCompId = 'plan_audit_validate';
var condition = function () {
    return ((jQuery.cookie('myplan_audit_running') != null) || (coerceValue('planAudit.campusParam') == '306' && coerceValue('planAudit.programParamSeattle') == 'default') || (coerceValue('planAudit.campusParam') == '310' && coerceValue('planAudit.programParamBothell') == 'default') || (coerceValue('planAudit.campusParam') == '323' && coerceValue('planAudit.programParamTacoma') == 'default'));
};
disabledCheck(disableCompId, 'action', condition);
jQuery('button#' + disableCompId).subscribe('AUDIT_COMPLETE', function () {
    var condition = function () {
        return ((coerceValue('planAudit.campusParam') == '306' && coerceValue('planAudit.programParamSeattle') == 'default') || (coerceValue('planAudit.campusParam') == '310' && coerceValue('planAudit.programParamBothell') == 'default') || (coerceValue('planAudit.campusParam') == '323' && coerceValue('planAudit.programParamTacoma') == 'default'));
    };
    disabledCheck(jQuery(this).attr('id'), 'action', condition);
});
