jq('#deleteCourseYesButton').subscribe('PLAN_ITEM_DELETED', function(data) {
    if (jq(this).is(':visible')){
        fnCloseAllPopups();
    }
}).subscribe('ERROR', function(data) {
        fnDisplayMessage(data.message, data.cssClass, 'deleteCourseYesButton', true);
});
