property.value:
jq('.myplan-saved-courses-detail').subscribe('PLAN_ITEM_DELETED', function(data){
    if (data.planItemType === 'wishlist') {
        fnRemoveSavedItem(data.planItemId, 'myplan-saved-courses-detail');
        fnUpdateSavedCount( jq(this).find('.uif-sectionHeader .uif-headerText strong').text() );
    }
});

jq('#deletePlannedCourse').subscribe('PLAN_ITEM_DELETED', function(data){
    fnDisplayMessage(data.message, data.cssClass, 'deletePlannedCourse', true, true);
    setTimeout('fnCloseAllPopups()', 2000);
}).subscribe('ERROR', function(data) {
    fnDisplayMessage(data.message, data.cssClass, 'deletePlannedCourse', true, true);
});

if ( jq('#planned_courses_detail_list ul:not(.errorLines) li').length > 0 ) {
    var iMaxHeight = 0;
    jq('#planned_courses_detail_list ul:not(.errorLines) li').each(function() {
        if ( jq(this).height() > iMaxHeight ) {
            iMaxHeight = jq(this).height();
        }
    });
    jq('#planned_courses_detail_list ul:not(.errorLines) li').each(function() {
        jq(this).height(iMaxHeight);
    });
    jq('#planned_courses_detail_list').jCarouselLite({
        btnNext: '.myplan-carousel-next',
        btnPrev: '.myplan-carousel-prev',
        scroll: 4,
        start: parseFloat(jq("input[type='hidden'][id^='atp_start_index']").val()),
        afterEnd: function(a) { fnBuildTitle(a, 'uif-groupHeader', 'planned_courses_detail'); },
        initCallback: function(a) { fnBuildTitle(a, 'uif-groupHeader', 'planned_courses_detail'); }
    });
}

jq('#planned_courses_detail_list')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        fnAddPlanItem(data.atpId, data.planItemType, data.planItemId, data.courseDetails.code, data.courseDetails.courseTitle, data.courseDetails.credit);
    })
    .subscribe('PLAN_ITEM_DELETED', function(data){
        fnRemovePlanItem(data.atpId, data.planItemType, data.planItemId);
    })
    .subscribe('UPDATE_NEW_TERM_TOTAL_CREDITS', function(data){
        fnUpdateCredits(data.atpId, data.totalCredits);
    })
    .subscribe('UPDATE_OLD_TERM_TOTAL_CREDITS', function(data){
        fnUpdateCredits(data.atpId, data.totalCredits);
    });

jq('#addPlannedCourse')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        fnDisplayMessage(data.message, data.cssClass, 'addPlannedCourse', true, true);
        setTimeout('fnCloseAllPopups()', 5500);
    })
    .subscribe('ERROR', function(data){
        fnDisplayMessage(data.message, data.cssClass, 'addPlannedCourse', true, true);
    });

