jQuery('body')
    .subscribe('SECTION_ITEM_ADDED', function(data){
        var link = '<a href="inquiry?methodToCall=start&viewId=SingleTerm-InquiryView&term_atp_id=' + data.atpId.replace(/-/g,'.') + '">Added</a>';
        jQuery('#button_' + data.atpId + '_' + data.RegistrationCode).attr('title', data.SectionCode + ' is added. To delete or move, go to ' + data.termName + ' Plan.').unbind('click');
        fnDisplayMessage(link, 'fl-font-size-80 fl-text-align-center', 'button_' + data.atpId + '_' + data.RegistrationCode, false);
        if (data.ItemsToUpdate != null) {
            var items = data.ItemsToUpdate.split(',');
            jQuery.each(items, function (index, value) {
                jQuery('#button_' + data.atpId + '_' + items[index]).attr('title', data.PrimarySectionCode + ' is added. To delete or move, go to ' + data.termName + ' Plan.').unbind('click');
                fnDisplayMessage(link, 'fl-font-size-80 fl-text-align-center', 'button_' + data.atpId + '_' + items[index], false);
            });
        }
    });
jQuery(window).load(function () {
    myplanGetSectionEnrollment('/student/myplan/plan/enroll', {'courseId':'@{dataObject.courseSummaryDetails.courseId}'});
})

jQuery('#course_details_actions')
    .subscribe('PLAN_ITEM_ADDED', function(data){
        if (data.planItemType === 'wishlist') {
            fnDisplayMessage(data.message, data.cssClass, data.courseDetails.courseId+'_addSavedCourse', true, null, false, '@{dataObject.courseSummaryDetails.courseId}_bookmarked');
        }
    })
    .subscribe('PLAN_ITEM_DELETED', function(data){
        if (data.planItemType === 'wishlist' && data.courseDetails.courseId === '@{dataObject.courseSummaryDetails.courseId}') {
            fnRestoreDetailsAddButton(data.courseDetails.courseId);
        }
    });

jQuery('#@{dataObject.courseSummaryDetails.courseId}_addSavedCourse')
