jQuery('input[name=enableAdviserView]').on('change', function(e) {
    var form = jQuery('#popupForm');
    addHiddenDataToForm('methodToCall', 'planAccess', form.attr('id'));
    form.ajaxSubmit({headers: { 'cache-control': 'no-cache' }});
});

var condition = function () {
    return ( /([0-9]xx)/i.test( coerceValue('courseCd') ) );
};
