jQuery('#deleteCourseYesButton').subscribe('PLAN_ITEM_DELETED', function(data) {
    if (jQuery(this).is(':visible')){
        fnCloseAllPopups();
    }
}).subscribe('ERROR', function(data) {
        fnDisplayMessage(data.message, data.cssClass, 'deleteCourseYesButton', true, true);
});
