property.value:
jQuery('body').subscribe('SECTION_ITEM_ADDED', function(data) {
        fnToggleSectionAction('button_' + data.atpId + '_' + data.RegistrationCode, data.RegistrationCode, 'added', data);
        if (data.ItemsToUpdate && data.Primary == 'false') {
            var items = data.ItemsToUpdate.split(',');
            jQuery.each(items, function (index, value) {
                fnToggleSectionAction('button_' + data.atpId + '_' + items[index], items[index], 'added', data, true);
            });
        }
    })
    .subscribe('SECTION_ITEM_DELETED', function(data) {
        if (jQuery('#' + 'button_' + data.atpId + '_' + data.RegistrationCode).data('initial')) {
            fnRemoveSectionRow('button_' + data.atpId + '_' + data.RegistrationCode);
            if (data.ItemsToUpdate) {
                var items = data.ItemsToUpdate.split(',');
                jQuery.each(items, function (index, value) {
                    fnRemoveSectionRow('button_' + data.atpId + '_' + items[index]);
                });
            }
        } else {
            fnToggleSectionAction('button_' + data.atpId + '_' + data.RegistrationCode, data.RegistrationCode, 'deleted', data);
            if (data.ItemsToUpdate) {
                var items = data.ItemsToUpdate.split(',');
                jQuery.each(items, function (index, value) {
                    fnToggleSectionAction('button_' + data.atpId + '_' + items[index], items[index], 'deleted', data);
                });
            }
        }
    });
    fnQuarterNavigation('@{T(org.kuali.student.myplan.plan.util.AtpHelper).getPreviousAtpId(dataObject.atpId)}', 'previous_quarter_button', 'single_quarter_items');
    fnQuarterNavigation('@{T(org.kuali.student.myplan.plan.util.AtpHelper).getNextAtpId(dataObject.atpId)}', 'next_quarter_button', 'single_quarter_items');

var tableHead = jQuery('table#@{dataObject.atpId.replace('.','-')} thead');
